mapper.update:
UPDATE post

    user_id=#{userId}
    type=#{type}
    text=#{text}
    image_ids=#{imageIds}
    video_id=#{videoId}

WHERE id=#{id}

mapper.select:
SELECT p.* FROM post p JOIN post_like pl ON p.id = pl.post_id
WHERE pl.user_id = #{userId}
LIMIT #{offset},#{limit}

SELECT p.* FROM post p JOIN user_follow uf ON p.user_id = uf.following_id
WHERE uf.follower_id = #{userId}

    AND p.id < #{beforeId}


    AND p.id > #{afterId}


    LIMIT #{limit}

ORDER BY p.id DESC
